static char current[128];

void layers_render() {
    // static const char PROGMEM kb_state_img[][16] = {{0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00}, {0x7e, 0x42, 0x5a, 0x42, 0x00, 0x7e, 0x00, 0x50, 0x40, 0x00, 0x76, 0x08, 0x04, 0x52, 0x00, 0x00}, {0x00, 0x00, 0x5e, 0x52, 0x42, 0x42, 0x52, 0x72, 0x06, 0x60, 0x4e, 0x4a, 0x42, 0x42, 0x4a, 0x7a}};
    uint8_t target = get_highest_layer(layer_state);
    memcpy(current, images[target], sizeof(images[0]));

    uint8_t t_char          = rand() % 32;
    uint8_t t_bit           = rand() % 8;
    uint8_t t_width         = rand() % 8 + 3;
    uint8_t t_direction     = rand() % 2;
    uint8_t t_global_offset = rand() % 2;

    if (rand() % 4 == 0) {
        for (uint16_t i = 0; i < 128; i++) {
            current[i] <<= t_global_offset;
            if ((i % 32) < t_char || (i % 32) > (t_char + t_width)) continue;
            if (t_direction) {
                current[i] <<= t_bit;
            } else {
                current[i] >>= t_bit;
            }
        }
    }

    oled_set_cursor(0, 6);
    oled_write_raw(current, sizeof(current));

    // oled_set_cursor(0, 8);

    // // if (host_keyboard_led_state().caps_lock) {
    // //     print("caps");

    // // } else {
    // //     print("no caps");
    // // }
    // oled_write_raw_P(kb_state_img[host_keyboard_led_state().caps_lock ? 1 : 0], sizeof(images[0]));
}
